args:
set -e
set -u
set -o pipefail
NAME="${input:csesName}"
ID="${input:csesId}"
lower=${NAME:l}
kebab=$(sed -E "s/[^a-z0-9]+/-/g; s/^-+|-+$//g" <<< "$lower")
file="${ID}-${kebab}.cpp"
if [ -e "$file" ]; then echo "File '$file' already exists. Aborting."; exit 1; fi
cat > "$file" <<EOF
#include <bits/stdc++.h>
using namespace std;

// $NAME
// https://cses.fi/problemset/task/$ID

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    return 0;
}
EOF
echo "Created $file"
